draw((1/2,1)--(1,0));

label("$0$", (0,0), S);
label("$1$", (1,0), S);
label("$a$", (1,0), E);
label("$0$", (0,0), W);
label("$1$", (0,1), W);
label("$b$", (0,1), N);
