def is_prime(n):
    """Check if a number is prime."""
    if n <= 1:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

# Generate the set of all prime numbers less than 30
primes_less_than_30 = [n for n in range(2, 30) if is_prime(n)]
